import './App.css';
import React, { Component } from 'react';
import Form from './components/Form'
import uniqid from "uniqid";

class App extends Component {
  constructor() {
    super();

    this.state = {
      education: {
        schoolName: '',
        degree: '',
        start: '',
        end: '',
        id: uniqid(),
      },
      educationExperiences: [],
      work: {
        company: '',
        position: '',
        mainTasks: '',
        start: '',
        end: '',
        id: uniqid(),
      },
      workExperiences: [],
    }
  }

  //Add Education and Work Tiles to template
  addEducation = () => {
    this.setState({
      educationExperiences: this.state.educationExperiences.concat(this.state.education),
    })
  }

  addWork = () => {
    this.setState({
      workExperiences: this.state.workExperiences.concat(this.state.work),
    })
  }

  //Remove Education and Work Tiles from template
  removeEducation = (id) => {
    this.setState({
      educationExperiences: this.state.educationExperiences.filter(item => item.id !== id),
    });
  }

  render() {
    const { educationExperiences, workExperiences } = this.state;

    return (
      <div>
        <Form
          addWork={this.addWork}
          workExperiences={workExperiences}
          addEducation={this.addEducation}
          educationExperiences={educationExperiences}
        />
      </div>
    )
  }
}

export default App;
