import './App.css';
import React, { Component } from 'react';
import Form from './components/Form'

class App extends Component {
  constructor() {
    super();

    this.state = {

      education: {
        schoolName: '',
        city: '',
        degree: '',
        start: '',
        end: '',
      },

      educationExperiences: [],
    }
  }

  addEducation = () => {
    this.setState({
      educationExperiences: this.state.educationExperiences.concat(this.state.education),
    })
  }

  render() {
    const { educationExperiences } = this.state;

    return (
      <Form addEducation={this.addEducation} educationExperiences={educationExperiences} />
    )
  }
}

export default App;
